mapper.update:
update stu_auth
set is_deleted = 1
where uname = #{studentId}

update stu_auth
set passwd     = #{passwd},
    is_deleted = #{isDeleted}
where uname = #{uname}

mapper.select:
select id, uname, passwd, is_deleted
from stu_auth
where uname = #{studentId}

mapper.update:
update stu_auth
set is_bound   = 1,
    wx_open_id = #{wxOpenId}
where uname = #{uname}
